import { generateFileTree } from "./tree_generator.js";

// Función para generar el contenido del README
export const generateREADME = (answers) => {

    const fileTree = generateFileTree(); // genera la estructura
    return `
## ${answers.projectName || 'Título no proporcionado'} 🚀

## Descripción 📄
${answers.description || 'Descripción no proporcionada'}

## Estructura de Archivos 🗂️
\`\`\`
${fileTree}
\`\`\`




## Tecnologías 💻
${answers.technologies.split(',').map(tech => `- ${tech.trim()}`).join('\n')}

## Instalación ⚙️
\`\`\`
${answers.installation}
\`\`\`

## Uso 🛠️
\`\`\`
${answers.usage}
\`\`\`

## Autor 👤
${answers.author}

## 📄 Licencia

Este proyecto está bajo la licencia ${answers.license}.

`;
}